//获取当前时间的小时和分钟的和
int GetHourAndMinute()
{
	int retTime = 1;
	time_t now;
	struct tm *newTime,timenow;
	newTime = &timenow;
	time( &now );
	localtime_r( &now,newTime );
	retTime = newTime->tm_hour*100 + newTime->tm_min;
	return retTime;
}

//取当前时间 格式: 2008-05-14 15:12:38
std::string GetTimeCurrent()
{
	char buf[128];
	memset(buf, 0, sizeof(buf));
	time_t now;
	struct tm *newTime,timenow;
	newTime = &timenow;
	time( &now );
	localtime_r( &now,newTime );



	sprintf(buf, "%4d-%02d-%02d %02d:%02d:%02d", newTime->tm_year+1900, newTime->tm_mon+1, newTime->tm_mday, newTime->tm_hour, newTime->tm_min, newTime->tm_sec);

	/*
	struct timeval tv;
	gettimeofday(&tv,NULL);
	sprintf(buf, "# %4d-%02d-%02d %02d:%02d:%02d:%06d:%d # ", \
		newTime->tm_year+1900, newTime->tm_mon+1, newTime->tm_mday, \
		newTime->tm_hour, newTime->tm_min, newTime->tm_sec, tv.tv_usec, tv.tv_sec);*/

	std::string strTime(buf);
	return strTime;
}

