//取CPU利用率
void GetSysInfo_Cpu()
{
	//执行脚本
	FILE* fp=fopen("/proc/stat", "r");
	if(fp==NULL)
	{
		printf("执行cpu检测脚本失败!\r\n");
		return;
	}
	//缓冲数据
	memset(g_chSysBuffer,0,sizeof(g_chSysBuffer));
	fread(g_chSysBuffer,sizeof(g_chSysBuffer),1,fp);
	fclose(fp);

	//printf("%s \r\n",g_chSysBuffer);


	float user,nice,sys,idle,iowait,irq,softirq;
	char cpu[21];
	sscanf(g_chSysBuffer,"%s %f %f %f %f %f %f %f",cpu,&user,&nice,&sys,&idle,&iowait,&irq,&softirq);
	//printf("user=%f,&nice=%f,&sys=%f,&idle=%f,&iowait=%f,&irq=%f,&softirq=%f\n",user,nice,sys,idle,iowait,irq,softirq);
	//利用率
	g_sysInfo.fCpu =  (( user + nice + sys+ iowait + irq + softirq - m_fUserOld)/(user + nice + sys + idle + iowait + irq + softirq- m_fTotalOld)) * 100;


	m_fUserOld = user + nice + sys + iowait + irq + softirq;

	m_fTotalOld = user + nice + sys + idle + iowait + irq + softirq;

	return;
}
