void ShowPCInfoToJHCSmallScreen()
{
	if(g_sysTime == time(NULL))
	{
		return;
	}
	g_sysTime = time(NULL);

	int fd;
	struct termios opt;

	char buf[128] = {0};
    sprintf(buf,"/dev/ttyS%d",g_ScreenComSetting.nComPort-1);

	fd = open(buf, O_RDWR);    //默认为阻塞读方式
	if(fd == -1)
	{
		perror("open serial 0\n");
		return;
	}

	//速率 9600
	tcgetattr(fd, &opt);
	cfsetispeed(&opt, B9600);
	cfsetospeed(&opt, B9600);

	if(tcsetattr(fd, TCSANOW, &opt) != 0 )
	{
		perror("tcsetattr error");
		close(fd);
		return;
	}

	//8位数据位
	opt.c_cflag &= ~CSIZE;
	opt.c_cflag |= CS8;
	//一位停止位
	opt.c_cflag &= ~CSTOPB;
	//无效验位
	opt.c_cflag &= ~PARENB;
	//opt.c_cflag &= ~INPCK;

	//无数据流控制
	opt.c_cflag &= ~CRTSCTS;




	opt.c_cflag |= (CLOCAL | CREAD);

	opt.c_lflag &= ~(ICANON | ECHO | ECHOE | ISIG);

	opt.c_oflag &= ~OPOST;
	opt.c_oflag &= ~(ONLCR | OCRNL);    //添加的
	opt.c_iflag &= ~(ICRNL | INLCR);
	opt.c_iflag &= ~(IXON | IXOFF | IXANY);    //添加的

	opt.c_cc[VTIME] = 1;
	opt.c_cc[VMIN] = 64;

	tcflush(fd, TCIOFLUSH);

	//printf("configure complete\n");

	if(tcsetattr(fd, TCSANOW, &opt) != 0)
	{
		perror("serial error");
		close(fd);
		return ;
	}
	//printf("start send and receive data\n");

	//清屏处理
	{
		char write_buf[4];
		memset(write_buf,0, sizeof(write_buf));
		write_buf[0] = 0xF8;
		write_buf[1] = 0x01;
		write_buf[2] = 0xA0;
		write(fd, write_buf, strlen(write_buf));
	}

	char buf1[20];
	char buf2[20];
	memset(buf1, 0, sizeof(buf1));
	memset(buf2, 0, sizeof(buf2));

	time_t now;
	struct tm *newTime,timenow;
	newTime = &timenow;
	time( &now );
	localtime_r( &now,newTime );

	sprintf(buf1, "%4d-%02d-%02d", newTime->tm_year+1900, newTime->tm_mon+1, newTime->tm_mday);
	sprintf(buf2, "%02d:%02d:%02d", newTime->tm_hour, newTime->tm_min, newTime->tm_sec);


	//设置第一行坐标
	{
		char write_buf[256];
		memset(write_buf,0, sizeof(write_buf));
		write_buf[0] = 0xF8;
		write_buf[1] = 0x80;
		write_buf[2] = 0x03;
		write_buf[3] = 0xA0;
		//printf("**** size=%d ****\n",(int)strlen(write_buf));
		write(fd, write_buf, strlen(write_buf));
		//printf("write %d chars\n",n);
	}


	//第一行
	{
		//printf("time:%s \n",buf1);
		char write_buf[256];
		memset(write_buf,0, sizeof(write_buf));
		write_buf[0] = 0xF8;
		write_buf[1] = 0x03;
		for(int i=0;i<(int)strlen(buf1);i++)
		{
			write_buf[i+2] = buf1[i];
		}
		write_buf[strlen(buf1)+2] = 0xA0;
		//printf("**** size=%d ****\n",(int)strlen(write_buf));
		write(fd, write_buf, strlen(write_buf));
	}

	//设置第二行坐标
	{
		char write_buf[256];
		memset(write_buf,0, sizeof(write_buf));
		write_buf[0] = 0xF8;
		write_buf[1] = 0x80;
		write_buf[2] = 0x44;
		write_buf[3] = 0xA0;
		//printf("**** size=%d ****\n",(int)strlen(write_buf));
		write(fd, write_buf, strlen(write_buf));
		//printf("write %d chars\n",n);
	}

	//第二行
	{
		//printf("time:%s \n",buf2);
		char write_buf[256];
		memset(write_buf,0, sizeof(write_buf));
		write_buf[0] = 0xF8;
		write_buf[1] = 0x03;
		for(int i=0;i<(int)strlen(buf2);i++)
		{
			write_buf[i+2] = buf2[i];
		}
		write_buf[strlen(buf2)+2] = 0xA0;
		//printf("**** size=%d ****\n",(int)strlen(write_buf));
		write(fd, write_buf, strlen(write_buf));
		//printf("write %d chars\n",n);
	}


	close(fd);
}
