//utf8转unicode
void UTF8ToUnicode(wchar_t *pwchOut, std::string strText)
{
	char *pText = (char *) strText.c_str();

	char* pOut = (char *)pwchOut;

	int i = 0, j = 0;
	while (i < strText.size())
	{
		if((pText[i] & 0x80) == 0x0)
		{
			pOut[j] = pText[i];
			i++;
		}
		else
		{
			pOut[j+1] = ((pText[i] & 0x0F) << 4) + ((pText[i+1] >> 2) & 0x0F);
			pOut[j] = ((pText[i+1] & 0x03) << 6) + (pText[i+2] & 0x3F);
			i += 3;
		}

		j += sizeof(wchar_t);
	}
}

