//根据时间判断白天还是晚上(随着季节的变化而变化，开关灯时间和算法中需要的白天晚上时间分开设置）
//nMode = 0表示算法白天晚上时间判别，nMode = 1表示开关灯时间判别
int DayOrNight(int nMode)
{
        int nDayNight = 1;
		time_t now = time(NULL);
		struct tm *newTime,timenow;
		newTime = &timenow;
		localtime_r( &now,newTime );

		unsigned int nDayHour = 0;
		unsigned int nNightHour = 0;

		//根据月份设置不同的时间
		int month = newTime->tm_mon+1;
		if(month>=3&&month<=5)  //春季
		{
		    if(nMode == 0)
		    {
                nDayHour = 530;
                nNightHour = 1745;
		    }
		    else if(nMode == 1)
		    {
                nDayHour = (g_LightTimeInfo.nSpringLightTime >> 16);
                nNightHour = (g_LightTimeInfo.nSpringLightTime & 0xffff);
		    }
		}
		else if(month>=6&&month<=8)//夏季
		{
            if(nMode==0)
		    {
                nDayHour = 500;
                nNightHour = 1830;
		    }
            else if(nMode == 1)
		    {
                nDayHour = (g_LightTimeInfo.nSummerLightTime >> 16);
                nNightHour = (g_LightTimeInfo.nSummerLightTime & 0xffff);
		    }
		}
		else if(month>=9&&month<=11)//秋季
		{
		   if(nMode==0)
		    {
                nDayHour = 530;
                nNightHour = 1730;
		    }
            else if(nMode == 1)
		    {
                nDayHour = (g_LightTimeInfo.nAutumnLightTime >> 16);
                nNightHour = (g_LightTimeInfo.nAutumnLightTime & 0xffff);
		    }
		}
		else //冬季
		{
            if(nMode==0)
		    {
                nDayHour = 600;
                nNightHour = 1715;
		    }
            else if(nMode == 1)
		    {
                nDayHour = (g_LightTimeInfo.nWinterLightTime >> 16);
                nNightHour = (g_LightTimeInfo.nWinterLightTime & 0xffff);
		    }
		}

        unsigned int uTime = newTime->tm_hour*100+newTime->tm_min;
        //LogTrace(NULL, "uTime=%u,nDayHour=%d,nNightHour=%d,nMode=%d,month=%d\n",uTime,nDayHour,nNightHour,nMode,month);
		if((uTime>=nDayHour)&&(uTime<=nNightHour))
		{
		    nDayNight = 1;
		}
		else
		{
            nDayNight = 0;
		}

	return nDayNight;
}

int mac_addr_sys(u_char *addr)
{
/* implementation for Linux */
    struct ifreq ifr;
    struct ifreq *IFR;
    struct ifconf ifc;
    char buf[1024];
    int s, i;
    int ok = 0;

    s = socket(AF_INET, SOCK_DGRAM, 0);
    if (s==-1) {
        return -1;
    }

    ifc.ifc_len = sizeof(buf);
    ifc.ifc_buf = buf;
    ioctl(s, SIOCGIFCONF, &ifc);

    IFR = ifc.ifc_req;

    for (i = ifc.ifc_len / sizeof(struct ifreq); --i >= 0; IFR++) {

        strcpy(ifr.ifr_name, IFR->ifr_name);
        if (ioctl(s, SIOCGIFFLAGS, &ifr) == 0) {
            if (! (ifr.ifr_flags & IFF_LOOPBACK)) {
                if (ioctl(s, SIOCGIFHWADDR, &ifr) == 0) {
                    ok = 1;
                    break;
                }
            }
        }
    }

    close(s);
    if (ok) {
        bcopy( ifr.ifr_hwaddr.sa_data, addr, 6);
    }
    else {
        return -1;
    }
    return 0;
}

