//寻找进程号
int GetProcessCountByName( const char* Command ,int nSize, std::string &pid)
{
	int total_count = 0;
	char statname[128];
	char cmd[64];
	FILE *fp;
	DIR  *dirp;
	struct dirent   *entp;

	dirp = opendir("/proc");
	while ( (entp = readdir(dirp)) != NULL )
	{
		// skip non-numerical names
		if (!isdigit(entp->d_name[0]))
			continue;
		// open file "stat" and obtain required info
		snprintf(statname, sizeof(statname),
			"/proc/%s/stat", entp->d_name);

		if ( (fp = fopen(statname, "r")) == NULL)
			continue;
		fscanf(fp, "%*d (%[^)])", cmd );
		fclose(fp);
		if( strncmp(Command,cmd, min(15,nSize)) == 0 )
		{
		    total_count ++;
		    pid = entp->d_name;
            break;
		}

	}
	closedir(dirp);
	return total_count;
}

//杀死testcarnum.out进程
void KillProcess(std::string strProcessName,int nMaxCount)
{
    for(int i = 0;i<nMaxCount;i++)
    {
        std::string pid;
        char cmdline[256]={0};
        int count = 0;
        count = GetProcessCountByName(strProcessName.c_str(),strProcessName.size(),pid);
        if(count > 0)
        {
            sprintf(cmdline,"kill -9 %s",pid.c_str());
            system(cmdline);
            printf("cmdline=%s\n",cmdline);
        }
        else
        {
            break;
        }
    }
}

