//车牌号码转换
void CarNumConvert(std::string& strCarNum,char wj[2])
{
    char chNumber = *(strCarNum.c_str());
	if(chNumber >= '0' && chNumber <= '9')//首字母不能为数字
	{
		return;
	}

	if(chNumber == 'i')//如果首字母是鄂
	{
		char chSecondNumber = *(strCarNum.c_str()+1);
		if(chSecondNumber == 'O')//O->0
		{
			strCarNum.replace(1,1,"0");
		}
	}

    int nIndex=0;
    if(chNumber!='L')
    {
		if(chNumber>='O' && chNumber<='Z')
		{
			// 空 军 北  二炮  成 南 海 兰  沈  广  济 直属   K  V  B   E    C  N  H  L   S   G   J Z(新军牌)
			switch(chNumber)
			{
				case 'O':
				strCarNum.replace(0,1,"K");
				break;
				case 'P':
				strCarNum.replace(0,1,"V");
				break;
				case 'Q':
				strCarNum.replace(0,1,"B");
				break;
				case 'R':
				strCarNum.replace(0,1,"E");
				break;
				case 'S':
				strCarNum.replace(0,1,"C");
				break;
				case 'T':
				strCarNum.replace(0,1,"N");
				break;
				case 'U':
				strCarNum.replace(0,1,"H");
				break;
				case 'V':
				strCarNum.replace(0,1,"L");
				break;
				case 'W':
				strCarNum.replace(0,1,"S");
				break;
				case 'X':
				strCarNum.replace(0,1,"G");
				break;
				case 'Y':
				strCarNum.replace(0,1,"J");
				break;
				case 'Z':
				strCarNum.replace(0,1,"Z");
				break;
			}
		}
		else if(chNumber != '#')
		{
			if(chNumber>='a')
			{
				nIndex = chNumber-'a';
			}
			else
			{
				nIndex = 26+chNumber-'A';
			}
			strCarNum.erase(0,1);
			strCarNum.insert(0,g_strCarNum.c_str()+3*nIndex,3);
		}
    }
    else
    {
		printf("CarNumConvert strCarNum=%s,wj[0]=%c,wj[1]=%c\n",strCarNum.c_str(),wj[0],wj[1]);
        strCarNum.replace(0,1,"W");
        //strCarNum.insert(2,wj,2);
		if(strCarNum.size() > 1)
		{
			chNumber = wj[0];
			if(chNumber>='a')
			{
				nIndex = chNumber-'a';
			}
			else
			{
				nIndex = 26+chNumber-'A';
			}

			if(nIndex >= 0 && nIndex < g_strCarNum.size())
			{
				strCarNum.insert(2,g_strCarNum.c_str()+3*nIndex,3);
			}
		}
		else
		{
			LogError("strCarNum=%s,wj[0]=%c,wj[1]=%c\n",strCarNum.c_str(),wj[0],wj[1]);
		}
    }
    /////////////////////////////////////最后一位警字,学字处理
    int nsize = strCarNum.size()-1;
    chNumber = *(strCarNum.c_str()+nsize);
    if(chNumber=='$')//学
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'M'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
    else if(chNumber=='%')//警
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'N'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
    else if(chNumber=='+')//澳
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'I'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
    else if(chNumber=='-')//港
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'J'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
    else if(chNumber=='@')//挂
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'H'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
    else if(chNumber=='(')//试
    {
        strCarNum.erase(nsize,1);
        nIndex = 26+'G'-'A';
        strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
    }
	else if(chNumber==')')//领
	{
		strCarNum.erase(nsize,1);
		nIndex = 26+'F'-'A';
		strCarNum.append(g_strCarNum.c_str()+3*nIndex,3);
	}
}

