//删除目录以及目录下的所有文件
void RemoveDir(const char *dirName)
{
	DIR *dir;
	struct dirent *pdirent = NULL;
	char chFileName[256]={0};
	if ((dir = opendir(dirName)) == NULL)
	{
		return;
	}
    while ((pdirent=readdir(dir)) != NULL)
    {
		if( strcmp(".", pdirent->d_name) == 0 || strcmp("..", pdirent->d_name) == 0 )
		{
			continue;
		}
		struct stat st;
		memset(chFileName, 0, 256);
       sprintf(chFileName, "%s/%s", dirName, pdirent->d_name);
       stat(chFileName, &st);
	   if(S_ISDIR(st.st_mode))//目录
	   {
		   RemoveDir(chFileName);
	   }
	   else
	   {
		  remove(chFileName);
	   }
    }
	closedir(dir);
	rmdir(dirName);//删除空目录
}


