//取得操作系统信息
void GetOsInfo()
{
	char buf[256] = {0};
	//发行版本
	FILE* fp = fopen("/etc/issue", "r");
	if(fp!=NULL)
	{
		fread(buf,sizeof(buf),1,fp);
		string tmp(buf);
		int pos = tmp.find(" ");
		pos = tmp.find(" ", pos+1);
		g_OsRelese = tmp.substr(0, pos);
		fclose(fp);
	}

	//主机名
	fp = fopen("/proc/sys/kernel/hostname", "r");
	if(fp!=NULL)
	{
		memset(buf, 0, sizeof(buf));
		fread(buf,sizeof(buf),1,fp);
		char *p = strtok(buf, "\n");
		g_HostName = p;
		fclose(fp);
	}

	//内核版本
	fp = fopen("/proc/sys/kernel/osrelease", "r");
	if(fp!=NULL)
	{
		memset(buf, 0, sizeof(buf));
		fread(buf,sizeof(buf),1,fp);
		char *p = strtok(buf, "\n");
		g_KernelRelese = string("Linux ") + p;
		fclose(fp);
		printf("g_KernelRelese=%s\n",g_KernelRelese);
	}

	//磁盘编号
	system("hdparm -i /dev/sd? | grep SerialNo >diskSerialNoTemp.log");
	fp = fopen("diskSerialNoTemp.log", "r");
	if(fp!=NULL)
	{
		memset(buf, 0, sizeof(buf));
		fread(buf,sizeof(buf),1,fp);
		string str(buf);
		//将\t换成空格
		for (int pos = str.find("\t"); pos != string::npos; pos = str.find("\t"))
		{
			str.replace(pos, 1, " ");
		}

		int pos1 = str.find("Model=");
		int pos2 = str.find(",");
		int pos3 = str.find("SerialNo=");
		int pos4 = str.find("\n");
		while (pos1 != string::npos)
		{
			string tmpName = str.substr(pos1+6, pos2-pos1-6);
			//删除双空格
			for (int pos = tmpName.find("  "); pos != string::npos; pos = tmpName.find("  "))
			{
				tmpName.erase(pos, 1);
			}
			if ("" == g_strDiskName)
				g_strDiskName = tmpName;
			else
				g_strDiskName += "; " + tmpName;

			tmpName = str.substr(pos3+9, pos4-pos3-9);
			//删除空格
			for (int pos = tmpName.find(" "); pos != string::npos; pos = tmpName.find(" "))
			{
				tmpName.erase(pos, 1);
			}

			if ("" == g_strDiskId)
				g_strDiskId = tmpName;
			else
				g_strDiskId += "; " + tmpName;

			pos1 = str.find("Model=", pos1+1);
			pos2 = str.find(",", pos1);
			pos3 = str.find("SerialNo=", pos1);
			pos4 = str.find("\n", pos1);
		}
		fclose(fp);
	}
	remove("diskSerialNoTemp.log");

	//文件系统 挂载路径
	fp = fopen("/proc/mounts", "r");
	if(fp!=NULL)
	{
		string str;
		char buf[1024];
		fread(buf,sizeof(buf),1,fp);
		bool flag = false;

		char *p = strtok(buf, "\n");
		while(NULL != p)
		{
			str = p;

			//将\t换成空格
			for (int pos = str.find("\t"); pos != string::npos; pos = str.find("\t"))
			{
				str.replace(pos, 1, " ");
			}

			//删除多个空格
			for (int pos = str.find("  "); pos != string::npos; pos = str.find("  "))
			{
				str.erase(pos, 1);
			}

			if (str.substr(0, 4) == "/dev")
			{
				int pos1, pos2, pos3;
				pos1 = str.find(" ");
				pos2 = str.find(" ", pos1+1);
				pos3 = str.find(" ", pos2+1);

				//挂载路径
				if( "" == g_strDiskMountPath )
					g_strDiskMountPath = str.substr(pos1+1, pos2-pos1-1);
				else
					g_strDiskMountPath += "; " + str.substr(pos1+1, pos2-pos1-1);

				//文件系统
				if ("" == g_strDiskFileSys)
					g_strDiskFileSys = str.substr(pos2+1, pos3-pos2-1);
				else
					g_strDiskFileSys += "; " + str.substr(pos2+1, pos3-pos2-1);

			}
			p = strtok(NULL, "\n");
		}
		fclose(fp);
	}
}

//系统运行时间
int GetSystemUptime()
{
	char buf[256] = {0};
	char *p = NULL;
	FILE* fp = fopen("/proc/uptime", "r");
	if(fp!=NULL)
	{
		fread(buf,sizeof(buf),1,fp);
		p = strtok(buf, " ");
		fclose(fp);
	}

	int seconds = atoi (p);
	int days = seconds/3600/24;
	return days;
}

//系统运行时间（XX days XX hours XX minutes XX seconds ）
string MyGetSystemUptime()
{
	char buf[256] = {0};
	char *p = NULL;
	FILE* fp = fopen("/proc/uptime", "r");
	if(fp!=NULL)
	{
		fread(buf,sizeof(buf),1,fp);
		p = strtok(buf, " ");
		fclose(fp);
	}

	int seconds = atoi (p);
	int days = seconds/3600/24;

	int hours = (seconds%(3600*24))/3600;

	int minutes = (seconds%3600)/60;

	int sec = seconds%60;
	memset(buf,0,256);
	sprintf(buf,"system has been runed: %d days %d hours %d minutes %d seconds\n",days,hours,minutes,sec);

	string tmp = buf;

	return tmp;
}

//取虚拟内存
void GetSwapMem(int* used, int* total)
{
	char buf[256] = {0};
	//发行版本
	FILE* fp = fopen("/proc/swaps", "r");
	if(fp!=NULL)
	{
		memset(buf, 0, sizeof(buf));
		fread(buf,sizeof(buf),1,fp);
		fclose(fp);


		string str = buf;
		//删除第一行
		int pos = str.find("\n");
		str.erase(0, pos+1);

		//删除双空格
		for (int pos = str.find("  "); pos != string::npos; pos = str.find("  "))
		{
			str.erase(pos, 1);
		}

		//将\t换成空格
		for (int pos = str.find("\t"); pos != string::npos; pos = str.find("\t"))
		{
			str.replace(pos, 1, " ");
		}

		//去除前2个字段
		pos = str.find(" ");
		str.erase(0, pos+1);
		pos = str.find(" ", pos);
		str.erase(0, pos+1);

		pos = str.find(" ");
		*total = atoi(str.substr(0, pos).c_str());
		int pos2 = str.find(" ", pos+1);
		*used = atoi(str.substr(pos, pos2-pos).c_str());
	}
}

