//判断网卡是否已连接 连接:1、未连接:0
int check_nic(char *nic_name)
{
	struct ifreq ifr;
	int skfd = socket(AF_INET, SOCK_DGRAM, 0);

	strcpy(ifr.ifr_name, nic_name);
	if (ioctl(skfd, SIOCGIFFLAGS, &ifr) < 0)
	{
		return -1;
	}
	if(ifr.ifr_flags & IFF_RUNNING)
	{
		return 1;
	}
	else
	{
		return 0;
	}
}

//获得网卡上行和下行速度
void GetRxTX(int &rx, int & tx)
{
	char RX[100] = {0};
	char TX[100] = {0};

	FILE *fp;
	char *cmd="ifconfig";
	char buf[1024];
	buf[1024]='\0';

	if((fp=popen(cmd,"r"))==NULL)
		perror("popen");
	while((fgets(buf,1024,fp))!=NULL)
	{
		if (buf[0] == 'e' && buf[1] == 't' && buf[2] == 'h' && buf[3] == '1')
		{
			memset(buf, '\0', sizeof(buf));
			while((fgets(buf,1024,fp))!=NULL)
			{

				int index = 0;
				while(index < strlen(buf) - 1)
				{
					if (buf[index++] == 'R')
					{
						break;
					}

				}
				if (buf[index-1] == 'R' && buf[index] == 'X' && buf[index+1] == ' ' && buf[index+2] == 'b' && buf[index+3] == 'y' && buf[index+4] == 't' && buf[index+5] == 'e' &&  buf[index+6]== 's' && buf[index+7] == ':')
				{
					index = index+8;
					int index2 = 0;
					while(buf[index] != ' ')
					{
						RX[index2++] =  buf[index++];

					}
					while(buf[index] != ':')
					{
						index++;
					}
					index2 = 0;
					index++;
					while(buf[index] != ' ')
					{
						TX[index2++] =  buf[index++];

					}

					pclose(fp);

					long long lrx = atoi(RX);
					long long ltx = atoi(TX);
					rx = (int)(lrx/1024);
					tx = (int)(ltx/1024);
					printf("RX:%d, TX:%d\n", rx, tx);
					return ;
				}
				memset(buf, '\0', sizeof(buf));
			}
		}
		break;
	}

}

