//根据通道编号来获取车道限速列表
void GetMapMaxSpeedStrByChan(mapMaxSpeedStr &mapMaxSpeedStrTmp, int nChanId)
{
	if(nChanId <= 0)
	{
		//LogNormal("Err nChanId=%d, GetMapMaxSpeedStrByChan!", nChanId);
		return;
	}

	MvDspGlobalSetting dspSettingStrc;
	MaxSpeedStr maxSpeedStrTmp;
	CXmlParaUtil xml;
	if(xml.LoadDspSettingFile(dspSettingStrc,nChanId))
	{
		for(int j = 0;j<dspSettingStrc.nChannels;j++)
		{
			maxSpeedStrTmp.nRadarAlarmVelo = dspSettingStrc.ChnlRegion[j].nRadarAlarmVelo;
			maxSpeedStrTmp.uAlarmBig = dspSettingStrc.ChnlRegion[j].uAlarmBig;
			maxSpeedStrTmp.uAlarmSmall = dspSettingStrc.ChnlRegion[j].uAlarmSmall;

			//LogNormal("GetMapMaxSpeedStrByChan-[%d,%d,%d]\n", maxSpeedStrTmp.nRadarAlarmVelo, maxSpeedStrTmp.uAlarmBig, maxSpeedStrTmp.uAlarmSmall);

			mapMaxSpeedStrTmp.insert(make_pair(dspSettingStrc.ChnlRegion[j].nVerRoadIndex, maxSpeedStrTmp));
		}
	}
	else
	{
		//LogNormal("--LoadDspSettingFile false! nChanId=%d \n", nChanId);
	}
}

//根据车道编号来获取车道限速
UINT32 GetMaxSpeedStrByRoadId(mapMaxSpeedStr &mapMaxSpeedStrTmp, const int uType, const int nRoadId)
{
	UINT32 nSpeed = 0;

	if (mapMaxSpeedStrTmp.size() <= 0)
	{
		//LogError("通道不存在，或者没有限速的配置文件\n");
		return 0;
	}
	mapMaxSpeedStr::iterator it = mapMaxSpeedStrTmp.find(nRoadId);
	if (it != mapMaxSpeedStrTmp.end())
	{
		MaxSpeedStr maxSpeedStrTemp = it->second;

		//LogNormal("maxSpeedStrTemp-[B %d, S %d,A %d]-\n", maxSpeedStrTemp.uAlarmBig, maxSpeedStrTemp.uAlarmSmall, maxSpeedStrTemp.nRadarAlarmVelo);
		if(maxSpeedStrTemp.uAlarmBig > 0 || maxSpeedStrTemp.uAlarmSmall > 0)
		{
			//根据车辆类型处理
			if(SMALL_CAR == uType)//小 1
			{
				nSpeed = maxSpeedStrTemp.uAlarmSmall;
			}
			else if(BIG_CAR == uType)//大 3
			{
				nSpeed = maxSpeedStrTemp.uAlarmBig;
			}
			else{}
		}
		else
		{
			nSpeed = maxSpeedStrTemp.nRadarAlarmVelo;
		}
	}
	else
	{
		//LogError("未找到车道号：%d\n",nRoadId);
		return 0;
	}

	//LogNormal("GetMaxSpeedStrByRoadId-nSpeed=%d uType=%d nRoadId=%d \n", nSpeed, uType, nRoadId);
	return nSpeed;
}

//根据车亮类型，通道号，车道号获取相应车道的限速值
UINT32 GetMaxSpeed(const int uType, const int nChannel, const int nRoadId)
{
	UINT32 nSpeed = 0;

	mapMaxSpeedStr mapMaxSpeedStrTmp;
	mapMaxSpeedStrTmp.clear();

	if(g_nDetectMode == 2)
	{
		GetMapMaxSpeedStrByChan(mapMaxSpeedStrTmp, nChannel);
	}
	else
	{
		CXmlParaUtil xml;
		xml.GetMaxSpeedStr(mapMaxSpeedStrTmp,nChannel);
	}

	nSpeed = GetMaxSpeedStrByRoadId(mapMaxSpeedStrTmp, uType, nRoadId);


	//LogNormal("GetMaxSpeed-nSpeed=%d \n", nSpeed);
	return nSpeed;
}

//写文件多次完成
bool SafeWrite(FILE *fpOut, const char *pBuffer, const int nFrameSize)
{
	bool bRet = false;
	if(fpOut)
	{
		int iWrited = 0;
		int iWrite = 0;
		int iCount = 0;

		while(iCount < 20)
		{
			iWrite = fwrite(pBuffer + iWrited, 1, nFrameSize - iWrited, fpOut);
			iWrited += iWrite;

			if(iWrited == nFrameSize)
			{
				//(iWrite == (nFrameSize - iWrited))
				bRet = true;
				break;
			}
			else
			{
				LogNormal("write file [%d] writed <%d bytes> all:%d \n", iCount, iWrited, nFrameSize);
			}

			iCount++;
		}

		//fwrite(pBuffer, 1, nFrameSize, fpOut);
	}

	return bRet;
}

