std::string FtpFormatConvert(std::string strFtpFmt,RECORD_PLATE* sPlate)
{
	char strRet[1024] = {0};
	char strBuf[10] ={0};
	std::string strBufTmp;
	std::string strInput;

	int nIndex = -1;
	const char* strKey[] = {"$YEAR","$MONTH","$DAY","$HOUR","$PLACE","$DETECTORID","$ROADID","$DIRECT"};
	const int nKeyIndex[] = {5,6,4,5,6,11,7,7};

	if (strFtpFmt.size() == 0 )
	{
		return strFtpFmt;
	}
	int nPos1 = -1;
	int nPos2 = -1;
	nPos1 = strFtpFmt.find("$");
	while(nPos1 >= 0)
	{
		if ((strFtpFmt.size() - nPos1) >=3)
		{
			sprintf(strBuf,"%s",strFtpFmt.substr(nPos1,3).c_str());
			strBufTmp = strBuf;
			if (strBufTmp.compare("$YE") == 0)
			{
				nIndex = 0;
				strInput = GetTime(sPlate->uTime, 9);
			}
			else if (strBufTmp.compare("$MO") == 0)
			{
				nIndex = 1;
				strInput = GetTime(sPlate->uTime, 14);
			}
			else if (strBufTmp.compare("$DA") == 0)
			{
				nIndex = 2;
				strInput = GetTime(sPlate->uTime, 15);
			}
			else if (strBufTmp.compare("$HO") == 0)
			{
				nIndex = 3;
				strInput = GetTime(sPlate->uTime, 5);
			}
			else if (strBufTmp.compare("$PL") == 0)
			{
				nIndex = 4;
				strInput = sPlate->chPlace;
			}
			else if (strBufTmp.compare("$DE") == 0)
			{
				nIndex = 5;
				strInput = g_strDetectorID;
			}
			else if (strBufTmp.compare("$RO") == 0)
			{
				nIndex = 6;
				strInput = g_ftpRemotePath;
			}
			else if (strBufTmp.compare("$DI") == 0)
			{
				nIndex = 7;
				strInput = GetDirection(sPlate->uDirection);
			}
			else
			{
				nIndex = -1;
			}
			if (nIndex >= 0)
			{
				memset(strRet,0,1024);
				nPos2 = strFtpFmt.find(strKey[nIndex]);
				if (nPos2 == 0)
				{
					sprintf(strRet,"%s%s",strInput.c_str(),strFtpFmt.substr(nPos2 + nKeyIndex[nIndex],strFtpFmt.size() - (nPos2 + nKeyIndex[nIndex]) + 1).c_str());
				}
				else if (nPos2 > 0)
				{
					sprintf(strRet,"%s%s%s",strFtpFmt.substr(0,nPos2).c_str(),strInput.c_str(),strFtpFmt.substr(nPos2 + nKeyIndex[nIndex],strFtpFmt.size() - (nPos2 + nKeyIndex[nIndex]) + 1).c_str());
				}
				if (strlen(strRet) > 0)
				{
					strFtpFmt = strRet;
				}
			}
		}
		nPos1 = strFtpFmt.find("$",nPos1+1);
	}

	return strFtpFmt;
}

//递归删除目录-hujx
bool RemoveDir2( const char* dirname)
{
	DIR* dir=NULL;
	struct dirent* ptr=NULL;
	dir = opendir(dirname);
	//printf("dirname=%s\n",dirname);

	if(dir)
	{
		// 初始化为0
		char buf[1024] = {0};
		while((ptr = readdir(dir))!=NULL)
		{
			if((strcmp(".",ptr->d_name)!=0)&&
				(strcmp("..",ptr->d_name)!=0))
			{
				sprintf(buf,"%s/%s",dirname,ptr->d_name);
				std::string strPath(buf);
				//printf("strPath=%s\n",strPath.c_str());

				//判断是否为目录
				struct stat st;
				stat(strPath.c_str(),&st);
				int n = S_ISDIR(st.st_mode);

				if(n==1)//目录
				{
					RemoveDir2(strPath.c_str());
				}
				else if(n==0) //文件
				{
					//umount(strPath.c_str());
					remove(strPath.c_str());
				}
			}
		}
		closedir(dir);
	}

	if(dirname)
		rmdir(dirname);

	return true;
}

// 判断是否闰年
int IsLeapYear(int year)
{
	if ((year % 4 == 0 && year % 100 != 0) || (year % 400 == 0))
	{
		return 1;
	}
	return 0;
}

// 根据月份判断日是否有效
int CheckDayValide(int nDay, int nMonth, int nYear)
{
	if (nDay > 31 || nDay < 1)
	{
		return 0;
	}
	// 对于日和月对应不上的，应舍弃
	if (nDay >= 31 && (nMonth == 1 || nMonth == 3 || nMonth == 5 || nMonth == 8 ||
		nMonth == 8 || nMonth == 10 || nMonth == 12))
	{// 日为31号，但月份不对
		return 0;
	}
	else if (nMonth == 2)
	{
		if (IsLeapYear(nYear) == 1) // 闰年
		{
			if (nDay > 29)
			{
				return 0;
			}
		}
		else if (nDay > 28) // 非闰年
		{
			return 0;
		}					
	}
	return 1;
}

void intToByte(unsigned char abyte0[4],int i)
{

	abyte0[0] = (unsigned char) (((0xff000000 & i) >> 24) + 0);

	abyte0[1] = (unsigned char) (((0xff0000 & i) >> 16) + 0);

	abyte0[2] = (unsigned char) (((0xff00 & i) >> 8) + 0);

	abyte0[3] = (unsigned char) ((0xff & i) + 0);
}

